package com.piinfo.service;import com.jcabi.ssh.Shell;import com.jcabi.ssh.SshByPassword;import org.springframework.stereotype.Service;import java.io.IOException;import java.net.UnknownHostException;public class SSH {	/**	 * In case a different Port is used	 */	private final int port;	/**	 * Ip address of the pi	 */	private final String ip;	/**	 * User to use for ssh connection	 */	private final String user;	/**	 * Password to use for the user for the ssh connection	 */	private final String passwd;	/**	 * Holds connection to pi	 */	private Shell shell;	public SSH (String Ip, String User, String Passwd, int Port) throws UnknownHostException {		ip = Ip;		user = User;		passwd = Passwd;		port = Port;		shell = new SshByPassword(ip, port, user, passwd);	}	/**	 * Make sure that the Class has been correctly instantiated	 *	 * @return Temperature of the Wifi? chip	 */	public String Temp () throws IOException {		return (Double.parseDouble(runCmd("cat /sys/class/thermal/thermal_zone0/temp")) / 1000.0 + "").substring(0, 5) + (" Â°C");	}	/**	 * Make sure, that this is NEVER called directly, as that could compromise the security	 *	 * @param cmd Execute that command and give the output	 * @return command output	 */	public String runCmd (String cmd) throws IOException {		return new Shell.Plain(shell).exec(cmd);	}	public String cpuUsage () throws IOException {		return (runCmd("echo $[100- $(vmstat 1 2 | tail -1 | awk '{print $15}')]").substring(0, 2) + "%");	}}